import axios from 'axios'
import React,{ useState } from 'react';
import { Redirect,Link } from 'react-router-dom';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import './Login.scss';
const baseUrl = 'http://localhost:8080';
const loginURL = `${baseUrl}/login`;

const Login = () => {
    const [isLoggedIn, setLogin]= useState(false);
    const [isLoginError, setLoginError]=useState(false);
    const [errorMessage, setErrorMessage]=useState("");

    login = (event) =>{
        event.preventDefault();
        if(event.username.value === "" || event.password.value === ""){
            setLoginError(true);
            setErrorMessage("You must provide a username and password");
            // isLoginError: true,
            // errorMessage: "You must provide a username and password."
            return;
        }
    
        axios.post(loginURL,{
          username: event.username.value,
          password: event.password.value
        })
        .then((response)=>{
          if(response.data.error){
            setLoginError(true);
            setErrorMessage(response.data.error.message);
            //   isLoginError: true,
            //   errorMessage: response.data.error.message
            return;
          }
          sessionStorage.setItem("authToken", response.data.token)
          setLogin(true);
          setLoginError(false);
            // isLoggedIn: true,
            // isLoginEroor: false
        })
        .catch((err)=>{
          console.log(err);
        });
    }

    renderLogin = () => {
       // const { isLoginError, errorMessage } = this.state
        return (

          <div className="login">
            <header className="login__header">Login</header>
            {isLoginError && <label style={{color: 'red'}}>{errorMessage}</label>}

            <Form ref="form">
              <Form.Group>
                <Form.Label className='form-label' htmlFor="username">Username: </Form.Label>
                <Form.Control type='text' name='username' placeholder="Enter username"/>
              </Form.Group>  
              <Form.Group>
                <Form.Label className='form-label' htmlFor="password">Password: </Form.Label>
                <Form.Control type='password' name='password' placeholder="Enter Password"/>
              </Form.Group>
              {/* <Button className='btn btn-primary' onClick={login()}>
                Login
              </Button> */}
            </Form>
          <Link className="login__links" to="/howitworks">Learn how the application works</Link>
          <Link className="login__links" to="register">Need to register?</Link>
          </div>
        )
      }

    return (
        <div>
            
        </div>
    );
};

export default Login;
